%% Ângulo pela aceleração (acelerômetro) e pela velocidade angular (giroscópio)
a=arduino('COM6','Uno');                    % Declaração do objeto arduino com a porta e modelo
imu=mpu6050(a);
ams=100;                                    % Quantidade de amostras
ang_gyro=0;                                 % Ângulo do giroscóio inicial

fig = figure();
fig.WindowState = 'maximized';
xlabel('Tempo','FontSize',16);
ylim([-100 100]); ylabel('Ângulo (º)','FontSize',16);
set(gca,'fontsize',14)
hold on; grid on;

for i=1:ams
    gyro_aux=readAngularVelocity(imu);      % Leitura do giroscópio
    gyro(i,:)=gyro_aux;
    
    ang_gyro=ang_gyro+gyro(i,1)*0.1;        % Ângulo pelo giroscópio
    angulo_gyro(i,:)=(ang_gyro*180/pi);
%     p1=plot(1:i,angulo_gyro(1:i,1),'-r','linewidth',1.5);
    
    acele_aux=readAcceleration(imu)/g;      % Leitura das acelerações
    acele_n_cali(i,:)=acele_aux;

    % Calculado as acelerações calibradas
    acele_x_cali(i,:)=(acele_n_cali(i,1)-offset_x)/ganho_x;
    acele_y_cali(i,:)=(acele_n_cali(i,2)-offset_y)/ganho_y;
    acele_z_cali(i,:)=(acele_n_cali(i,3)-offset_z)/ganho_z;

    angulo_acele(i,:)=atand(acele_y_cali(i,:)/sqrt(acele_x_cali(i,:)^2+acele_z_cali(i,:)^2)); % Ângulo pela aceleração em graus
%     p2=plot(1:i,angulo_acele(1:i),'-b','linewidth',1.5);
    
%     legend([p1,p2],'Ângulo-giroscópio','Ângulo-acelerômetro','FontSize',12);
end

